import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as d,c as r,d as a}from"./app-f8288c12.js";const e={},h=a('<h1 id="humberman-lab" tabindex="-1"><a class="header-anchor" href="#humberman-lab" aria-hidden="true">#</a> Humberman Lab</h1><h2 id="工具箱总结" tabindex="-1"><a class="header-anchor" href="#工具箱总结" aria-hidden="true">#</a> 工具箱总结</h2><p>在看过Humberman lab和雷军的《小米创业思考》</p><ol><li>专注力</li><li>精力</li><li>情绪</li><li>动力</li><li>快</li></ol><table><thead><tr><th>动力</th><th>内容</th></tr></thead><tbody><tr><td>物质类</td><td></td></tr><tr><td>- 咖啡因饮品</td><td>促进多巴胺的吸收(茶, 咖啡,马黛茶)</td></tr><tr><td>- 抽烟</td><td>短暂的多巴胺提升</td></tr><tr><td>- L-酪氨酸补品</td><td></td></tr><tr><td>- 碳水</td><td>包子, 汉堡等(注意增肥和困倦的副作用)</td></tr><tr><td>行为类</td><td></td></tr><tr><td>- 做事时听音乐</td><td></td></tr><tr><td>- 每天早上晒太阳5-10分钟</td><td></td></tr><tr><td>- 冷水澡</td><td></td></tr><tr><td>- 戒断&quot;奶头乐&quot;</td><td>做无聊的事,不刷手机等恢复多巴胺</td></tr><tr><td>- 强化成长信念</td><td>当做事痛苦时, 强调这是自己的选择, 为了成长愿意吃苦. 做完会得到战胜自己的快乐,即多巴胺释放, 有利于下一次迎接痛苦</td></tr><tr><td>- 程序化想象</td><td>做事前想象步骤, 强调完成后的状态. 多巴胺可以通过想象分泌. 预设心理, 强调困难任务和奖励的链接, 强化奖励回路</td></tr><tr><td>- 在睡醒8小时内做高习惯强度任务, 次8小时做低习惯强度任务</td><td>睡醒的前8小时, 多巴胺上升, 情绪稳定, 经历充沛. 后8小时逐渐下降</td></tr></tbody></table><p>关于&quot;程序化想象&quot;多说一句: 往往有&quot;三分钟热度时&quot;, 这时候, 我们自动进行着程序化想象, 对未来充满憧憬, 多巴胺激情分泌, 动力十足. 当<br> 经受到打击后. 我们往往不愿意, 或者说无法维持对未来的憧憬. 这时候需要自己重新找到执行任务的意义(要不激进)并刻意训练&quot;做任务--&gt; 奖励的回路&quot;</p><table><thead><tr><th>精力</th><th>内容</th></tr></thead><tbody><tr><td>睡眠管理</td><td></td></tr><tr><td>- 听喜马拉雅</td><td>听有些兴趣, 但不至于兴奋的小说(如三体, 杀死一只知更鸟, 月亮与六便士等)</td></tr><tr><td>- 安眠药</td><td>心理安慰, 半片即可</td></tr><tr><td>- 冥想式睡眠</td><td>意识扫描躯体, 转移注意力. 张嘴诱使打哈欠</td></tr><tr><td>- 冷水澡</td><td>降低体温</td></tr><tr><td>- 看悬疑视频</td><td></td></tr><tr><td>- 预睡眠</td><td>提前半小时准备睡眠, 看一些无聊的书(如linux)</td></tr><tr><td>饮食管理</td><td></td></tr><tr><td>- 不吃碳水</td><td>饱腹感但不困. 如吃巧克力, 鸡胸肉, 凉菜等</td></tr><tr><td>日常精神力的恢复</td><td></td></tr><tr><td>- 冷水澡</td><td>同时增强动力, 缓解情绪</td></tr><tr><td>- 闭目听歌</td><td>听3首歌左右</td></tr><tr><td>- 小憩</td><td>20分钟左右, 睡着为佳. 同时缓解情绪</td></tr><tr><td>- 有氧运动</td><td>10分钟左右的有氧运动, 室内来回跑步, 减轻50%左右症状</td></tr></tbody></table><table><thead><tr><th>情绪</th><th>内容</th></tr></thead><tbody><tr><td>第一步</td><td>远离情绪源. 如跟老婆吵架了, 先远离她. 学习也是同理. 先远离压力源</td></tr><tr><td>第二步(在第一步的基础上进行)</td><td></td></tr><tr><td>- 冷水澡</td><td>同时缓解精力, 增强动力</td></tr><tr><td>- 躺在床上</td><td>闭上嘴巴, 也不思考, 让情绪自己逐渐平静</td></tr><tr><td>- 做任何转移注意力的事</td><td>不能有很大难度, 要抓注意力. 如打游戏(英雄联盟/PUBG). 跟老婆吵架可以用, 学习, 睡眠导致的焦虑不能用, 会更不好学习</td></tr><tr><td>- 吃好吃的, 公园跟老婆散步</td><td></td></tr></tbody></table>',8),o=[h];function l(i,b){return d(),r("div",null,o)}const c=t(e,[["render",l],["__file","humberman_lab.html.vue"]]);export{c as default};
